      }
      {edit && dataset && thing && (
        <input
          type="text"
          // eslint-disable-next-line react/jsx-props-no-spreading
          {...inputProps}
          onChange={(e) => {
            setValue(e.target.value);
          }}
          onBlur={(e) => autosave && saveHandler(e)}
          value={value}
        />
      )}
    </>
  );
