import React, { ReactElement, useEffect, useState, useContext } from "react";
import { overwriteFile, CommonProperties, useProperty } from "../../helpers";
import { SessionContext } from "../../context/sessionContext";
import useFile from "../../hooks/useFile";

export type Props = {
  maxSize?: number;
  inputProps?: React.InputHTMLAttributes<HTMLInputElement>;
  errorComponent?: React.ComponentType<{ error: Error }>;
  loadingComponent?: React.ComponentType | null;
} & CommonProperties &
  React.VideoHTMLAttributes<HTMLVideoElement>;

/**
 * Fetches and displays a video, from a src found at a given property of a given Thing. Can also be used to upload a new/replacement video file.
 */
export function Video({
  property: propProperty,
  properties: propProperties,
  thing: propThing,
  edit,
  autosave,
  onSave,
  onError,
  maxSize,
  inputProps,
  errorComponent: ErrorComponent,
  loadingComponent: LoadingComponent,
  ...videoOptions
}: Props): ReactElement | null {
  const { fetch } = useContext(SessionContext);

  const values = useProperty({
    thing: propThing,
    property: propProperty,
    properties: propProperties,
    type: "url",
  });

  const { value, thing, error: thingError } = values;
  let valueError;
  if (!edit && !value) {
    valueError = new Error("No value found for property.");
  }

  const isFetchingThing = !thing && !thingError;

  const [error, setError] = useState<Error | undefined>(
    thingError ?? valueError
  );

  useEffect(() => {
    if (error) {
      if (onError) {
        onError(error);
      }
    }
  }, [error, onError, ErrorComponent]);

  const [videoObjectUrl, setVideoObjectUrl] = useState("");

  const {
    data,
    error: videoError,
    inProgress: fetchingVideoInProgress,
  } = useFile(value as string);

  useEffect(() => {
    if (fetchingVideoInProgress) {
      return;
    }
    if (videoError) {
      setError(videoError);
      return;
    }
    const videoObjectURL = data && URL.createObjectURL(data);
    if (videoObjectURL) {
      setVideoObjectUrl(videoObjectURL);
    }
  }, [data, fetchingVideoInProgress, videoError]);

  const handleChange = async (input: EventTarget & HTMLInputElement) => {
    const fileList = input.files;
    if (autosave && fileList && fileList.length > 0 && value) {
      const newObjectUrl = await overwriteFile(
        value as string,
        fileList[0],
        input,
        fetch,
        maxSize,
        onSave,
        onError
      );
      if (newObjectUrl) {
        setVideoObjectUrl(newObjectUrl);
      }
    }
  };

  let videoComponent = null;

  if (isFetchingThing || fetchingVideoInProgress) {
    let loader: JSX.Element | null = (LoadingComponent && (
      <LoadingComponent />
    )) || <span>fetching data in progress</span>;
    if (LoadingComponent === null) {
      loader = null;
    }
    return loader;
  }

  if (error) {
    videoComponent = ErrorComponent ? (
      <ErrorComponent error={error} />
    ) : (
      <span>{error.toString()}</span>
    );
  } else if (value) {
    videoComponent = (
      /* eslint jsx-a11y/media-has-caption: 0, react/jsx-props-no-spreading: 0 */
      <video
        src={videoObjectUrl || (value as string)}
        controls
        {...videoOptions}
      />
    );
  }

  return (
    <>
      {videoComponent}
      {edit && (
        <input
          // eslint-disable-next-line react/jsx-props-no-spreading
          {...inputProps}
          type="file"
          accept="video/*"
          onChange={(e) => handleChange(e.target)}
        />
      )}
    </>
  );
}
