/** @hidden */
function getAgentUrlAll(acr) {
    return Array.from(new Set(getThingAll(internal_getAcr(acr))
        .map((thing) => {
        return getUrlAll(thing, ACP.agent);
    })
        .reduce((flatArray, agentArray) => {
        return flatArray.concat(agentArray);
    }, [])));
}

